import React, {Component} from 'react'
import './App.css'

export default class App extends Component {
  constructor(props){
    super(props)
    this.state={
        files : [],
        id : []
    }
    this.onChange=this.onChange.bind(this);
    this.onClick = this.onClick.bind(this);
  }
  onChange(e) {
   const index = [];
  this.setState({files:e.target.files})
   for(var i =0; i<e.target.files.length;i++){
    index.push(i);
   }
   this.setState({id:index});
  }
  onClick(e){
    var images = [];
    var id = [];
    for(var i = 0; i<this.state.files.length; i++){
      if(e.target.id == this.state.id[i]){
       continue;
      }
      images.push(this.state.files[i]);
      id.push(this.state.id[i]);
    }
    this.setState({files:images,id:id});

  }
  render() {
   const images = [];
   let imgUrl;
    for(var i =0; i<this.state.files.length;i++){
      imgUrl = URL.createObjectURL(this.state.files[i]);  
      images.push(<img src={imgUrl} className="images" alt = "pic" id = {this.state.id[i]} onClick={(this.onClick)}/>);
    }

    return (
      < form id = "form">
        <div>
          <h2 id = "header" >Multiple Image holder</h2>
          <p>Upload as many images as you want</p>
          <p>Click on image to delete it!</p>
        </div>
        <br></br>
        <div id ="buttonHolder">
          <input type="file" id = "button" multiple onChange={(this.onChange)} />
          </div>
        <br></br>
        <div className="ImagesDiv">
          {images}
        </div>
      </form>
    )
  }
}
